import React,{createContext, useState, useEffect} from 'react'
import {v4 as uuidv4} from 'uuid';
import Axios from 'axios';


export const DataContext = createContext();

export const DataContextProvider = ({children}) => {

    const[data, setData] = useState([

    ])
    const getData = async() => {
        try {

            const response = await fetch("/transactions");
            const dataAsJSON = await response.json();

            setData(dataAsJSON);
        } catch (error) {
            console.log(error.message);
        }

    }

    useEffect(() => {
        getData();
    },[data.length]);





    const addExpense = async(type,total) =>{
        try {

            Axios.post('http://localhost:3001/trans/insert',
            {type:type,total:total
            }).then(()=>{
                alert("I DID IT");
            })


        } 
        catch(e){
           console.log(e.message) ;
        }
    }
    //     const response = await fetch("/transactions",{
        //         method:"POST",
        //         headers:{"Content-Type": "application/json"},
        //         body: JSON.stringify(body)
        //     });
        //     setData([...data,{type:body.type,total:body.amount,isincome:false,id:body.id}])
        // } catch (error) {
        //     console.log(error.message)
        // }
    const addIncome = async (type,total) =>{

        try {

            const body = {
                type:type,
                amount:total,
                isIncome: true,
                uuid: uuidv4()
            }

            const response = await fetch("/transactions",{
                method:"POST",
                headers:{"Content-Type": "application/json"},
                body: JSON.stringify(body)
            });
            console.log(response);
            setData([...data,{type:body.type,total:body.amount,isincome:true,uuid:body.uuid}])
        } catch (error) {
            console.log(error.message)
        }

    }
    const deleteData = async(uuid) =>{

        try {

            const deleteTrans = await fetch(`/transactions/${uuid}`,{
                method:"DELETE"
            });

            console.log(deleteTrans);
        } catch (error) {
            console.log(error.message);
        }
        const temp = data.filter(e => e.uuid !== uuid)
        setData(temp);
    }


    return (
        <DataContext.Provider value={{data,addExpense,addIncome,deleteData}}>
            {children}
        </DataContext.Provider>
    )
}

export default DataContextProvider;
