import {v4 as uuidv4} from 'uuid';
import Axios from 'axios';


export const DataContext = createContext();

export const DataContextProvider = ({children}) => {

    const[data, setData] = useState([

    ])
    const getData = () => {
        try {

                Axios.get('http://localhost:3001/trans/get').then((response) =>{
                setData(response.data)
                })

        } catch (error) {
            console.log(error.message);
        }

    }

    useEffect(() => {
        getData();
    },[data.length]);





    const addExpense = async(type,total) =>{
        try {

            Axios.post('http://localhost:3001/trans/insert',
            {type:type,
            total:total,
            isincome:false
            }).then(()=>{
                alert("I DID IT");

            })
        //  setData([...data,{type:type,total:total,isincome:false,trans_id: null}])
            getData();

        } 
        catch(e){
           console.log(e.message) 
        }
    }
    //     const response = await fetch("/transactions",{
        //         method:"POST",
        //         headers:{"Content-Type": "application/json"},
        //         body: JSON.stringify(body)
        //     });
        //     setData([...data,{type:body.type,total:body.amount,isincome:false,id:body.id}])
        // } catch (error) {
        //     console.log(error.message)
        // }
    const addIncome = async(type,total) =>{

        try {

            Axios.post('http://localhost:3001/trans/insert',
            {type:type,
            total:total,
            isincome:true
            }).then(()=>{
                alert("I DID IT");

            })
            //  setData([...data,{type:type,total:total,isincome:true,trans_id: null}])
            getData();
        } 
        catch(e){
           console.log(e.message) 
        }

    }
    const deleteData = async(id) =>{

        const trans_id = id;
        console.log(id);

       Axios.delete(`http://localhost:3001/trans/delete/${trans_id}`).then(()=>{

       })

        const temp = data.filter(e => e.trans_id !== id);
        setData(temp);
    }


    return (
        <DataContext.Provider value={{data,addExpense,addIncome,deleteData}}>
            {children}
        </DataContext.Provider>
    )
}

export default DataContextProvider;
